mapper.select:
/* carMapper.getCar */
SELECT * FROM car WHERE car_no =
#{car_no}

/* carMapper.getCarList */
SELECT * FROM car ORDER BY
car_no DESC

mapper.update:
/* carMapper.updateTireDistance */
UPDATE car SET
tire_change_distance=#{tire_change_distance} WHERE car_no=#{car_no}

/* carMapper.updateWiperDistance */
UPDATE car SET
wiper_change_distance=#{wiper_change_distance} WHERE car_no=#{car_no}

/* carMapper.updateCooler */
UPDATE car SET
cooler_left=#{cooler_left} WHERE car_no=#{car_no}

/* carMapper.updateEngineOil */
UPDATE car SET
engine_oil_viscosity=#{engine_oil_viscosity} WHERE car_no=#{car_no}

/* carMapper.updateDistance */
UPDATE car SET
distance=#{distance} WHERE car_no=#{car_no}
